mapper.update:
delete from sys_role_menu


		and roleId = #{roleId}


		and menuId = #{menuId}

mapper.select:
SELECT me.`id` AS id, me.`name` AS NAME, me.`url` AS url, me.`path` AS path, me.`css`, me.`createTime` AS createTime, p.`id` AS pId, p.`name` AS pName, p.`url` AS pUrl, p.`path` AS pPath, p.`css` AS pCss FROM sys_menu me LEFT JOIN sys_menu p LEFT JOIN sys_role_menu m ON p.`id` = m.menuId ON me.`id` = p.`parentId` WHERE m.roleId IN
#{roleId}
AND me.`hidden` = 0 AND p.`hidden` = 0 ORDER BY me.`sort` ASC
